import React, { useState, useEffect } from "react";
import Button from '@material-ui/core/Button'
import TextField from '@material-ui/core/TextField'
import Radio from '@material-ui/core/Radio'
import RadioGroup from '@material-ui/core/RadioGroup'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import FormControl from '@material-ui/core/FormControl'
import {
  makeStyles,
  withStyles,
  Snackbar,
} from "@material-ui/core";
import { ContactUsInfoAPIMethod } from "../../api/generalClient";


const styles = makeStyles((theme) => ({

  field: {
    marginTop: 20,
    marginBottom: 20,
    display: 'block'
  },
}));

const StyledButton = withStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.secondary.main,
    color: "white",
    width: "300px",
  },
  label: {
    textTransform: "capitalize",
    fontSize: "24px",
  },
}))(Button);

export default function MessageCenter() {

  useEffect(async () => {
    try {

    } catch (err) {
      alert(err.response);
    }
  }, []);
  const classes = styles();

  const [title, setTitle] = useState('')
  const [details, setDetails] = useState('')
  const [titleError, setTitleError] = useState(false)
  const [detailsError, setDetailsError] = useState(false)
  const [category, setCategory] = useState('money')
  const [open, setOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState("");
  const [status, setStatus] = useState("");

  const handleSubmit = async (e) => {
    try {
      e.preventDefault()
      setTitleError(false)
      setDetailsError(false)

      if (title == '') {
        setTitleError(true)
      }
      if (details == '') {
        setDetailsError(true)
      }
      if (title && details) {
        await ContactUsInfoAPIMethod({
          name: localStorage.getItem("name"),
          email: localStorage.getItem("email"),
          Date: new Date(),
          message: { title: details },

        });
        onClick_send(e);
      }
    }
    catch (err) {
      console.log("FrontEnd: ", err);
      setSnackbarMessage(err.response);
      setStatus("error");
      setOpen(true);
    }


  };
  const onClick_send = (e) => {
    setSnackbarMessage("Message Send!");
    setStatus("success");
    setOpen(true);
  };
  return (
    <form noValidate onSubmit={handleSubmit}>
      <TextField className={classes.field}
        onChange={(e) => setTitle(e.target.value)}
        label="Message Title"
        variant="outlined"
        color="secondary"
        fullWidth
        required
        error={titleError}
      />
      <TextField className={classes.field}
        onChange={(e) => setDetails(e.target.value)}
        label="Details"
        variant="outlined"
        color="secondary"
        multiline
        rows={4}
        fullWidth
        required
        error={detailsError}
      />
      <FormControl className={classes.field}>
        {/* <FormLabel>Notification Category</FormLabel> */}
        <RadioGroup value={category} onChange={(e) => setCategory(e.target.value)}>
          <FormControlLabel value="URGENT*" control={<Radio />} label="URGENT*" />
          <FormControlLabel value="Regular" control={<Radio />} label="Regular" />
          <FormControlLabel value="Payment" control={<Radio />} label="Payment" />
          <FormControlLabel value="User experience" control={<Radio />} label="User experience" />
        </RadioGroup>
      </FormControl>

      <StyledButton>Send</StyledButton>
      <Snackbar
        open={open}
        autoHideDuration={6000}
        onClose={handleClose}
        anchorOrigin={{ vertical: "bottom", horizontal: "right" }}
      >
        <Alert onClose={handleClose} severity={status}>
          {snackbarMessage}
        </Alert>
      </Snackbar>
    </form>
  );
}
