import { React, useState } from "react";
import Navbar from "../components/NavBar";
import { ContactUsInfoAPIMethod } from "../api/generalClient";

import {
  Grid,
  Container,
  makeStyles,
  Typography,
  Button,
  withStyles,
  TextField,
} from "@material-ui/core";

const styles = makeStyles((theme) => ({
  bodyContainer: {
    height: "80vh",
    width: "100vw",
    backgroundColor: theme.palette.primary.main,
    alignItems: "inline",
    display: "flex",
  },
  contact: {
    fontFamily: "Inter",
    fontWeight: "bold",
    fontSize: "64px",
    color: theme.palette.secondary.main,
  },
  input: {
    margin: "2%",
  },
}));
const StyledButton = withStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.secondary.main,
    color: "white",
    width: "300px",
  },
  label: {
    textTransform: "capitalize",
    fontSize: "24px",
  },
}))(Button);

const Contact = () => {
  const [name, setName] = useState(" ");
  const [email, setEmail] = useState(" ");
  const [message, setMessage] = useState(" ");

  const onChange_name = (e) => {
    e.preventDefault();
    setName(e.target.value);
  }

  const onChange_email = (e) => {
    e.preventDefault();
    setEmail(e.target.value);
  }

  const onChange_message = (e) => {
    e.preventDefault();
    setMessage(e.target.value);
  }

  const sendContactInfo = (e) => {
    e.preventDefault();
    console.log([name, email, message]);

    try {
      e.preventDefault();
      ContactUsInfoAPIMethod({ name: name, email: email, message: message });
    }
    catch (err) {
      alert(err.response);
    }
  }

  const classes = styles();
  return (
    <>
      <Navbar />
      <Container className={classes.bodyContainer}>
        <Grid
          container
          direction="column"
          justify="space-between"
          alignItems="center"
        >
          <Grid item>
            <Typography variant="h2" className={classes.contact}>
              Contact Us
            </Typography>
          </Grid>
          <Grid item style={{ width: "50%" }} direction="column">
            <TextField
              placeholder="John Doe"
              label="Name"
              fullWidth
              onChange={onChange_name}
              required
              className={classes.input}
            />

            <TextField
              placeholder="johndoe@email.com"
              label="Email"
              fullWidth
              onChange={onChange_email}
              required={true}
              className={classes.input}
            />

            <TextField
              placeholder="Enter your message"
              label="Message"
              fullWidth
              multiline
              rows={5}
              onChange={onChange_message}
              required={true}
              className={classes.input}
            />
          </Grid>
          <Grid item>
            <StyledButton onClick={sendContactInfo}>Send</StyledButton>
          </Grid>
        </Grid>
      </Container>
    </>
  );
};

export default Contact;
