import React, { useEffect, useState } from "react";
import Sidebar from "../components/SideBar";
import UserHeader from "../components/userHeader";
import Chart from "react-apexcharts";
import Masonry from 'react-masonry-css';


import ReactFusioncharts from "react-fusioncharts";
import FusionCharts from "fusioncharts";
import charts from "fusioncharts/fusioncharts.charts";

// Resolves charts dependancy


import {
  Grid,
  Paper,
  makeStyles,
} from "@material-ui/core";

const styles = makeStyles((theme) => ({
  root: {
    height: "100vh",
    width: "100vw",
  },
  rightbar: {
    display: "flex",
    height: "100%",
    width: "80%",
    backgroundColor: theme.palette.primary.main,
    alignItems: "center",
    justifyContent: "center",
  },
  rightContainer: {
    height: "95%",
    width: "95%",
    backgroundColor: theme.palette.primary.dark,
  },
  contentContainer: {
    height: "85%",
    width: "100%",
  },
}));

export default function ContactUserVAdmin() {
  charts(FusionCharts);


  const [isAdmin, setIsAdmin] = useState(JSON.parse(localStorage.getItem("isAdmin")));
  const classes = styles();

  //BAR graph
  const [optionsB, setOptions] = useState({ chart: { id: "basic-bar" }, xaxis: { categories: [1991, 1992, 1993, 1994, 1995, 1996, 1997, 1998] } });
  const [seriesB, setSeries] = useState([{ name: "series-1", data: [30, 40, 45, 50, 49, 60, 70, 91] }]);

  //DONUT graph
  const [optionsD, setOptionsD] = useState({ labels: ['Win', 'Loss', 'Tie'] });


  let x = localStorage.getItem("seriesD")
  const [seriesD, setSeriesD] = useState([Number(x[0]), Number(x[2]), Number(x[4])]);
  const breakpoints = {
    default: 4,
    1100: 3,
    700: 2,
    500: 1
  };
  const dataSource = {
    chart: {
      caption: "App Publishing Trend",
      subcaption: "2012-2016",
      xaxisname: "Years",
      yaxisname: "Total number of apps in store",
      formatnumberscale: "1",
      plottooltext:
        "<b>$dataValue</b> apps were available on <b>$seriesName</b> in $label",
      theme: "fusion"
    },
    categories: [
      {
        category: [
          {
            label: "2012"
          },
          {
            label: "2013"
          },
          {
            label: "2014"
          },
          {
            label: "2015"
          },
          {
            label: "2016"
          }
        ]
      }
    ],
    dataset: [
      {
        seriesname: "iOS App Store",
        data: [
          {
            value: "125000"
          },
          {
            value: "300000"
          },
          {
            value: "480000"
          },
          {
            value: "800000"
          },
          {
            value: "1100000"
          }
        ]
      },
      {
        seriesname: "Google Play Store",
        data: [
          {
            value: "70000"
          },
          {
            value: "150000"
          },
          {
            value: "350000"
          },
          {
            value: "600000"
          },
          {
            value: "1400000"
          }
        ]
      },
      {
        seriesname: "Amazon AppStore",
        data: [
          {
            value: "10000"
          },
          {
            value: "100000"
          },
          {
            value: "300000"
          },
          {
            value: "600000"
          },
          {
            value: "900000"
          }
        ]
      }
    ]
  };

  return (
    <Grid container alignItems="center" className={classes.root}>
      <Sidebar />
      <Grid item className={classes.rightbar}>
        <Paper className={classes.rightContainer}>
          <UserHeader page=" Statistics" />

          <Masonry
            breakpointCols={breakpoints}
            className="my-masonry-grid"
            columnClassName="my-masonry-grid_column">
            {isAdmin ? "Admin" : "USER"}

            <ReactFusioncharts
              type="mscolumn3d"
              // width="100%"
              // height="100%"
              dataFormat="JSON"
              dataSource={dataSource}
            />

            {/* <Chart
              options={optionsB}
              series={seriesB}
              type="bar"
              width="500"
            />

            <Chart options={optionsD} series={seriesD} type="pie" width="380" /> */}

          </Masonry>


        </Paper>
      </Grid>
    </Grid>
  );
}
