import React, { useState, useEffect } from "react";
import Sidebar from "../SideBar";

import Typography from '@material-ui/core/Typography'
import Button from '@material-ui/core/Button'
import TextField from '@material-ui/core/TextField'
import Radio from '@material-ui/core/Radio'
import RadioGroup from '@material-ui/core/RadioGroup'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import FormControl from '@material-ui/core/FormControl'

import {
  Grid,
  Paper,
  makeStyles,
  withStyles,
  Container,
} from "@material-ui/core";
// import { postMessagesAPIMethod } from "../api/adminClient";

import UserHeader from "../userHeader";

const styles = makeStyles((theme) => ({
  root: {
    height: "100vh",
    width: "100vw",
  },
  rightbar: {
    display: "flex",
    height: "100%",
    width: "80%",
    backgroundColor: theme.palette.primary.main,
    alignItems: "center",
    justifyContent: "center",
  },
  rightContainer: {
    height: "95%",
    width: "95%",
    backgroundColor: theme.palette.primary.dark,
  },
  contentContainer: {
    height: "85%",
    width: "100%",
  },
  field: {
    marginTop: 20,
    marginBottom: 20,
    display: 'block'
  },
}));

const StyledButton = withStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.secondary.main,
    color: "white",
    width: "300px",
  },
  label: {
    textTransform: "capitalize",
    fontSize: "24px",
  },
}))(Button);

export default function MessageCenter() {

  useEffect(async () => {
    try {

    } catch (err) {
      alert(err.response);
    }
  }, []);
  const classes = styles();

  const [title, setTitle] = useState('')
  const [details, setDetails] = useState('')
  const [titleError, setTitleError] = useState(false)
  const [detailsError, setDetailsError] = useState(false)
  const [category, setCategory] = useState('money')

  return (
    <Grid container alignItems="center" className={classes.root}>
      <Sidebar />
      <Grid item className={classes.rightbar}>
        <Paper className={classes.rightContainer}>
          <UserHeader page="Contact Admin" />


          <Container size="sm">


            <form noValidate>
              <TextField className={classes.field}
                onChange={(e) => setTitle(e.target.value)}
                label="Message Title"
                variant="outlined"
                color="secondary"
                fullWidth
                required
                error={titleError}
              />
              <TextField className={classes.field}
                onChange={(e) => setDetails(e.target.value)}
                label="Details"
                variant="outlined"
                color="secondary"
                multiline
                rows={4}
                fullWidth
                required
                error={detailsError}
              />
              <FormControl className={classes.field}>
                {/* <FormLabel>Notification Category</FormLabel> */}
                <RadioGroup value={category} onChange={(e) => setCategory(e.target.value)}>
                <FormControlLabel value="Regular" control={<Radio />} label="Regular" />
                  <FormControlLabel value="URGENT*" control={<Radio />} label="URGENT*" />
                  <FormControlLabel value="Payment" control={<Radio />} label="Payment" />
                  <FormControlLabel value="User experience" control={<Radio />} label="User experience" />
                </RadioGroup>
              </FormControl>


        <StyledButton>Send</StyledButton>
            </form>


          </Container>


        </Paper>
      </Grid>
    </Grid>
  );
}
